mapper.select:
SELECT sale_consultant_id AS consultantId,sale_consultant_name AS consultantName,COUNT(*) AS salesVolume
FROM sales_record 


	    vehicle_model_id = #{vehicleModelId}


	    AND dealer_id = #{dealerId}


GROUP BY sale_consultant_id
ORDER BY salesVolume DESC

SELECT vehicle_model_id,vehicle_model_code,vehicle_model_name,COUNT(*) AS num 
FROM sales_record
WHERE dealer_id = #{dealerId}

	AND buy_date BETWEEN #{start} AND #{end}	

AND market_activity_id = #{marketActivityId}
GROUP BY vehicle_model_id
